    this.test1 = this.test1.bind(this)
  }
  test1() {
    this.props.jbFn('注意言行！！！')
  }
  render() {
    return (
      <div>
        <div>
          {this.props.list.map((item, index) => {
            return item.type !== 4 ? (
              <div key={index} className="mesitem">
                {item.value}
